mapper.select:
		select anonyNo,title,writeDate,hit,likeCnt,id,writer,replyCnt
    from(
        select rownum rnum, anonyNo,title,writeDate,hit,likeCnt,id,writer,replyCnt
        from(
            select a.anonyNo,a.title,a.writeDate,a.hit,a.likeCnt,m.id,a.writer,a.replyCnt
            from anonymous a, member m
            where m.id=a.id
            order by anonyNo desc
        )
    )
 where rnum between #{startRow} and #{endRow}

select a.anonyNo, a.title, a.content, a.writer, m.id, a.writeDate, a.hit,a.likeCnt,a.replyCnt
from anonymous a, member m
where  a.id = m.id and a.anonyNo=#{anonyNo}

select anonyNo 
from anonymous_like
where id = #{id} and anonyNo = #{anonyNo}
