mapper.select:
select qnaNo, tourNo , region, title, id, status,hit, writeDate, refNo, ordNo, levNo,parentNo
from(
    select rownum rnum, qnaNo, tourNo ,region,  title, id, status,hit, writeDate,refNo, ordNo, levNo, parentNo
    from(
        	select DISTINCT q.qnaNo, q.tourNo, t.region,  q.title, m.id, q.status,q.hit, q.writeDate, q.refNo, q.ordNo, q.levNo,q.parentNo
            from qna q, tour t, member m
            where 1=1
            
            and t.no = q.tourNo 
            and m.id = q.id
            order by q.refNo desc, q.ordNo asc
        )
     )  
	 where rnum between #{startRow} and #{endRow}

