mapper.select:
	select qnaNo,tourNo, region, title, id, status,hit, writeDate, refNo, ordNo, levNo,parentNo
from(
    select rownum rnum, qnaNo, tourNo,region,  title, id, status,hit, writeDate,refNo, ordNo, levNo, parentNo
    from(
        select q.qnaNo, q.tourNo, t.region,  q.title, m.id, q.status,q.hit, q.writeDate, q.refNo, q.ordNo, q.levNo,q.parentNo
            from qna q, tour t, member m
            where (t.no = q.tourNo) and (q.id=m.id) and q.tourNo =#{tourNo}
            order by q.refNo desc, q.ordNo asc
        )
     )  where rnum between  #{pageObject.startRow} and #{pageObject.endRow}
